import React, { useState } from 'react';
import './App.css';

function App(){
	const API_KEY = `0b3b530e772fd76341de3dada4786a6b`;

	// state
	const [search, setSearch] = useState('adelaide');
	const [data, setData] = useState([]);


	// runs when search button is clicked
	const getData = () => {
		fetch(`http://api.openweathermap.org/data/2.5/weather?q=${search}&APPID=${API_KEY}&units=metric`)
			.then(response => response.json())
			.then(jsonResponse => console.log(jsonResponse))
			.then(res => setData(res))
			.catch(err => console.log('Something went wrong'))
		}

	const updateSearch = (event) => {
		setSearch(event.target.value);
		console.log('search: ' + search);
		if(event.keyCode === 13){
			getData();
		}
	}

	return (
		<div className = "App">
		  <h1>Weather App</h1>
		  	onSubmit = { getData }
	  	<input 
		  	name = "City Search" 
		  	type = "text" 
		  	placeholder = "Enter City"
		  	onChange = {updateSearch}
		  	value = {search}
 		/>
		<button 
			// type = 'submit'
		  	onClick = {getData}
		>
		  	Search
	  	</button>
		  <h1>
		  	The weather in {search} is...
		  </h1>
		</div>
	);
}



export default App;
