import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const Temperature = ({city, currentTemperature, maxTemperature, minTemperature, feelsLike}) => {
	if(city === ''){
		return(
			<div>
				<h1>No City Selected</h1>
			</div>
		);
	}

	return(
		<div>
	  		<Card>
		      <CardContent>
		        <Typography color="textSecondary" gutterBottom>
		          City: {city}
		        </Typography>
		        <Typography variant="h5" component="h2">
		          <strong/>Current Temperature:<strong/> {currentTemperature}
		        </Typography>
		        <Typography variant="h5" component="h2">
		          Feels Like: {feelsLike}
		        </Typography>
		        <Typography variant="h5" component="h2">
		          Maximum Temperature: {maxTemperature}
		        </Typography>
		        <Typography variant="h5" component="h2">
		          Minimum Temperature: {minTemperature}
		        </Typography>
		      </CardContent>
		      <CardActions>
		        <Button size="small">Learn More</Button>
		      </CardActions>
		    </Card>
	  	</div>
	);
}

export default Temperature;
