import React, { useState } from 'react';
import './App.css';

function App(){
	const API_KEY = `0b3b530e772fd76341de3dada4786a6b`;

	// state
	const [query, setQuery] = useState('adelaide');
	const [search, setSearch] = useState('');
	const [data, setData] = useState([]);


	// runs when search button is clicked
	const getData = () => {
		fetch(`http://api.openweathermap.org/data/2.5/weather?q=${query}&APPID=${API_KEY}&units=metric`)
			.then(response => response.json())
			.then(jsonResponse => console.log(jsonResponse))
			.then(res => setData(res))
			.catch(err => console.log('Something went wrong'))
		}

	const updateSearch = (event) => {
		setSearch(event.target.value);
		if(event.keyCode === 13){
			setQuery(event.target.value);
		}
	}

	return (
		<div className = "App">
		  <h1>Weather App</h1>
		  <form>
		  	<input 
			  	name = "City Search" 
			  	type = "text" 
			  	placeholder = "Enter City"
			  	onChange = {updateSearch}
	 		/>
			<button 
			  	onClick = {getData}
			>
			  	Search
		  	</button>

		  </form>
		  <h1>
		  	The weather in {query} is...
		  </h1>
		</div>
	);
}



export default App;
